const state = {
    secret: "panda", // allWordsArr[Math.floor(Math.random()*allWordsArr.length)],
    grid: Array(6)
        .fill()
        .map(() => Array(5).fill("")),
    currentRow: 0,
    currentCol: 0,
};

function genBox(container, row, col, letter = "") {
    // create box function
    const box = document.createElement("div");
    box.className = "box";
    box.id = `box${row}${col}`;
    box.textContent = letter;

    container.appendChild(box);
    return box;
}

function genGrid(container) {
    // create grid
    const grid = document.createElement("div");
    grid.className = "grid";

    // create boxes
    for (var i = 0; i < 6; i++) {
        for (var j = 0; j < 5; j++) {
            genBox(grid, i, j);
        }
    }
    // append grid (boxes)
    container.appendChild(grid);
}

function refreshGrid() {
    for (var i = 0; i < state.grid.length; i++) {
        for (var j = 0; j < state.grid[i].length; j++) {
            const box = document.querySelector(`#box${i}${j}`);
            box.textContent = state.grid[i][j];
        }
    }
}

