function start() {
    const board = document.querySelector(".board");
    genGrid(board);
    registerKey();
}

function isLetter(key) {
    return key.length === 1 && key.match(/[a-z]/i);
}

function registerKey() {
    document.body.onkeydown = (e) => {
        const key = e.key;
        if (key == "Enter") {
            if (state.currentCol == 5) {
                const word = getCurrentWord();
                // if(allWordsArr.includes(word)){
                if (wordOK) {
                    revealWord(word);
                    state.currentRow++;
                    state.currentCol = 0;
                } else {
                    window.alert("Tohle slovo neznám.");
                }
            }
        }
        if (key == "Backspace") {
            removeLetter();
        }
        if (key in equivalentLetters) {
            addLetter(key);
        }
        refreshGrid();
        // test()
    };
}

function getCurrentWord() {
    return state.grid[state.currentRow].reduce((prev, curr) => prev + curr);
}

function addLetter(letter) {
    if (state.currentCol == 5) return;
    state.grid[state.currentRow][state.currentCol] = letter;
    state.currentCol++;
}

function removeLetter(letter) {
    if (state.currentCol == 0) return;
    state.grid[state.currentRow][state.currentCol - 1] = "";
    state.currentCol--;
}

function revealWord(guess) {
    const row = state.currentRow;
    for (var i = 0; i < 5; i++) {
        const box = document.querySelector(`#box${row}${i}`);
        const letter = box.textContent;
        //console.log(letter)
        if (letter == state.secret[i]) {
            box.classList.add("right");
        } else if (state.secret.includes(letter)) {
            box.classList.add("wrong");
        } else {
            box.classList.add("empty");
        }
    }
    const isWinner = state.secret == guess;
    const isGameOver = state.currentRow == 5;

    if (isWinner) {
        window.alert("You won!");
    } else if (isGameOver) {
        window.alert(`You lost. The word was ${state.secreat}`);
    }
}

start();

function wordOK(w) {
    var pos = 0;
    while (1) {
        pos = allWords.indexOf(w, pos);
        if (pos < 0) break;
        if (pos % 5 == 0) return true;
        pos += 1;
    }
    srvLog("w", w);
    return false;
}
